function newCat(name, BornYear, LivesAt, colors, legs, size, FavouriteDish, initialSpeed, maxspeed, initialJump, maxJumpHeight) {

    this.name = name
    this.bornYear = bornYear
    this.LivesAt = livesAt
    this.colors = colors
    this.legs = legs
    this.size = size
    this.FavouriteDish = FavouriteDish
    this.initialSpeed = 0
    this.maxspeed = maxspeed
    this.initialJump = 0
    this.maxJumpHeight = maxJumpHeight

}