delete Array.prototype.includes

function includes(array, value, fromIndex) {
    var notFound = true

    for (var i = arguments.length < 3 ? 0 : fromIndex > -1 ? fromIndex : array.length + fromIndex; i < array.length && (notFound = array[i] !== value); i++);

    return !notFound
}

console.log('CASE 1')

var nums = [100, 200, 300, 400, 500]

var result = includes(nums, 600)

console.log(result)
// false

console.log('CASE 2')

var animals = ['pigs', 'goats', 'sheep', 'cows']

var result = includes(animals, 'sheep')

console.log(result)
// true

console.log('CASE 3')

var nums = [10, 20, 30]

var result = includes(nums, 30, 3)

console.log(result)
// false

console.log('CASE 4')

var nums = [10, 20, 30]

var result = includes(nums, 30, 1)

console.log(result)
// true

console.log('CASE 5')

var animals = ['pigs', 'goats', 'sheep', 'cows']

var result = includes(animals, 'cows', 2)

console.log(result)
// true

console.log('CASE 6')

var animals = ['pigs', 'goats', 'sheep', 'cows']

var result = includes(animals, 'goats', 2)

console.log(result)
// false


console.log('CASE 7')

var animals = ['pigs', 'goats', 'sheep', 'cows']

var result = includes(animals, 'goats', -3)

console.log(result)
// true

console.log('CASE 8')

var animals = ['pigs', 'goats', 'sheep', 'cows']

var result = includes(animals, 'goats', -2)

console.log(result)
// false